sqlMap.insert:

            insert into tb_calc   
              (time,rate,currency,debt,repayment,debtid,status,interest)  
              values (#time#,#rate#,#currency#,#debt#,#repayment#,#debtid#,#status#,#interest#);  


			SELECT
			last_insert_id() as id;

         insert into tb_calc(time,rate,currency,debt,repayment,debtid,status,interest) select tempdate,#rate#,#currency#,#debt#,0,#id#,1,#interest# from calendar where tempdate>#starttime# and tempdate<=#endtime#


			SELECT
			last_insert_id() as id;


            insert into tb_calc_flow  
              (interest,timestamp,status,Cashflowid,Calcid,currency,operation)  
              values (#interest#,#timestamp#,#status#,#cashflowid#,#calcid#,#currency#,#operation#);  


			SELECT
			last_insert_id() as id;

         insert into tb_calc_flow(interest,timestamp,status,Calcid,currency) select #interest#,tempdate,1,#id#,#currency# from calendar where tempdate>#starttime# and tempdate<=#endtime#


			SELECT
			last_insert_id() as id;


            insert into tb_log_calc   
              (interest,time,currency,flowid,operation,cashflowid)  
              values (#interest#,#time#,#currency#,#flowid#,#operation#,#cashflowid#);  


			SELECT
			last_insert_id() as id;


            insert into tb_syslog_calc   
              (interest,time,currency,flowid,operation,cashflowid)  
              values (#interest#,#time#,#currency#,#flowid#,#operation#,#cashflowid#);  


			SELECT
			last_insert_id() as id;
